	     html += '</tr>';	     $("#checkList").append(html);	 }else if (rec_module == 'ScoreGoods'||rec_module == 'PromoteGoods'){		 $("#div_largessList").show();		 var html = '<tr class="row" onmouseover="over(event)" onmouseout="out(event)"  id="tr' + rowkey+'">';	 		 html += '<td>'+goods_sn+'</td>';		 	     html += '<td >'+goods_name+'</td>';		 		 html += '<td >'+goods_attr+'</td>';	     html += '<td >'+num+'</td>';	     html += '<td >'+goods_score +'</td>';	     html += '<td >'+send_number+'</td>';	     html += '</tr>';	     $("#largessList").append(html);	}}$().ready(function() {	$("#div_largessList").hide();	<?php if(is_array($orderGoodsList)): $k = 0; $__LIST__ = $orderGoodsList;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$child): ++$k;$mod = ($k % 2 )?><?php		$goodsAttr = preg_replace("/\n|\r/Um","<br/>",$child["attr"]); 	?>	newTr('<?php echo ($child["rec_module"]); ?>', 			'<?php echo ($child["rec_id"]); ?>', 			'<?php echo ($child["data_sn"]); ?>',			'<?php echo ($child["data_name"]); ?>',			'<?php echo ($child["data_cost_price"]); ?>',			'<?php echo ($child["data_price"]); ?>',			'<?php echo ($child["number"]); ?>',			'<?php echo ($child["data_weight"]); ?>',			'<?php echo ($child["data_total_score"]); ?>',			'<?php echo ($child["send_number"]); ?>','<?php echo ($goodsAttr); ?>');<?php endforeach; endif; else: echo "" ;endif; ?>});</script>