  `bond_sn_prefix` varchar(50) default NULL COMMENT '团购券序号前缀',
  `seo_title` varchar(255) default '',
  `shelves_price` float(10,4) NOT NULL,
  `profit` float(10,4) NOT NULL,
  `quan_id` int(11) NOT NULL,
